(function($) {
  "use strict"; // Start of use strict

  // Smooth scrolling using jQuery easing
  $('a.js-scroll-trigger[href*="#"]:not([href="#"])').click(function() {
    if (location.pathname.replace(/^\//, '') == this.pathname.replace(/^\//, '') && location.hostname == this.hostname) {
      var target = $(this.hash);
      target = target.length ? target : $('[name=' + this.hash.slice(1) + ']');
      if (target.length) {
        $('html, body').animate({
          scrollTop: (target.offset().top - 56)
        }, 1000, "easeInOutExpo");
        return false;
      }
    }
  });

  // Closes responsive menu when a scroll trigger link is clicked
  $('.js-scroll-trigger').click(function() {
    $('.navbar-collapse').collapse('hide');
  });

  // Activate scrollspy to add active class to navbar items on scroll
  $('body').scrollspy({
    target: '#mainNav',
    offset: 57
  });

  // Collapse Navbar
  var navbarCollapse = function() {
	    if ($("#mainNav").offset().top > 100) {
	      $("#mainNav").addClass("");
	    } else {
	      $("#mainNav").removeClass("");
	    }
	  };
  // Collapse now if page is not at top
  navbarCollapse();
  // Collapse the navbar when page is scrolled
  $(window).scroll(navbarCollapse);

  // Scroll reveal calls
  window.sr = ScrollReveal();

  sr.reveal('.sr-icon-1', {
    delay: 200,
    scale: 0
  });
  sr.reveal('.sr-icon-2', {
    delay: 400,
    scale: 0
  });
  sr.reveal('.sr-icon-3', {
    delay: 600,
    scale: 0
  });
  sr.reveal('.sr-icon-4', {
    delay: 800,
    scale: 0
  });
  sr.reveal('.sr-button', {
    delay: 200,
    distance: '15px',
    origin: 'bottom',
    scale: 0.8
  });
  sr.reveal('.sr-contact-1', {
    delay: 200,
    scale: 0
  });
  sr.reveal('.sr-contact-2', {
    delay: 400,
    scale: 0
  });

 
  //toglle_product_card
  $('.inner_toggle').click(function() {
	  var status = $(this).find('i').attr('class');
	  if(status == 'fa fa-angle-down'){
			$(this).find('i').attr('class','fa fa-angle-up');
		}else{
			$(this).find('i').attr('class','fa fa-angle-down');
		}
  });
  
  $('.inner_toggle2').click(function() {
	  var status = $(this).find('#review_icon').attr('class');
	  if(status == 'fa fa-angle-down'){
			$(this).find('#review_icon').attr('class','fa fa-angle-up');
		}else{
			$(this).find('#review_icon').attr('class','fa fa-angle-down');
		}
  });
  
  //qna board sorting
  $('.qna_btns').click(function() {
	var btnValue = $(this).text();  
	alert(btnValue);
	$('#qna_table').find('.qna_topic').each(function(i, e) {
		var topicValue = $(this).text();
		if(btnValue == topicValue){
			$(this).parent('.qna_row').show();
			
		}
		if(btnValue != topicValue){
			$(this).parent('.qna_row').hide();
		}
	})
	  
  });
  
  $('.qna_btns_all').click(function() {
	$('#qna_table').find('.qna_row').show();
  })

  //side menu change
  $('.side_menu_contents').click(function() {
	var conText = $(this).text();
	$('#side_menu_title').text(conText);
  });
  
})(jQuery); // End of use strict
